	/**
	 * Techpert:Bahikhata : 0.0.1 :tests BahiKhataStringUtility.matchStringsSafely()
	 * 
	 */
	@Test
	public void testMatchStringsSafely() {
		String null1 = null;
		String null2 = null;
		String empty1 = "";
		String empty2 = "";
		String s1 = "abcd";
		String s2 = "abcd";
		String s3 = "xyz";
		assertTrue(BahiKhataStringUtility.matchStringsSafely(null1, null2));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(null1, empty1));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(null1, s1));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(s1, null2));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(empty2, null2));
		assertTrue(BahiKhataStringUtility.matchStringsSafely(empty1, empty2));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(empty1, s2));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(s2, empty2));
		assertTrue(BahiKhataStringUtility.matchStringsSafely(s1, s2));
		assertFalse(BahiKhataStringUtility.matchStringsSafely(s1, s3));
	}

	/**
	 * Techpert:Bahikhata : 0.0.1 :Tests
	 * BahiKhataStringUtility.isNotNullAndNotEmpty()
	 */
	@Test
	public void testIsNotNullAndNotEmpty() {
		String null1 = null;
		String empty1 = "";
		String s1 = "abcd";

		assertFalse(BahiKhataStringUtility.isNotNullAndNotEmpty(null1));
		assertFalse(BahiKhataStringUtility.isNotNullAndNotEmpty(empty1));
		assertTrue(BahiKhataStringUtility.isNotNullAndNotEmpty(s1));
	}

